import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';

function App() {
  const [documents, setDocuments] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [file, setFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [showPreviewModal, setShowPreviewModal] = useState(false);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/documents');
      setDocuments(res.data);
    } catch (err) {
      console.error('Error al obtener documentos:', err);
    }
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    if (selectedFile) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setPreviewUrl(event.target.result); // Generar URL temporal
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (title && description && file) {
      const formData = new FormData();
      formData.append('title', title); // Cambiado a "title" para coincidir con el backend
      formData.append('description', description); // Cambiado a "description" para coincidir con el backend
      formData.append('file', file);

      try {
        const res = await axios.post('http://localhost:4000/api/documents', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        alert(res.data.message);
        setTitle('');
        setDescription('');
        setFile(null);
        setShowUploadModal(false);
        fetchDocuments(); // Actualizar la lista de documentos
      } catch (err) {
        console.error('Error al subir documento:', err);
        alert('Error al subir documento');
      }
    } else {
      alert('Por favor complete todos los campos, incluyendo el archivo.');
    }
  };

  const openUploadModal = () => {
    setDate(new Date().toISOString().split('T')[0]); // Establecer la fecha actual
    setShowUploadModal(true);
  };

  const openPreview = () => {
    if (previewUrl) {
      setShowPreviewModal(true);
    } else {
      alert('Por favor selecciona un archivo para previsualizar.');
    }
  };

  return (
    <div className="app-content">
      <div className="container-fluid">
        <div className="row mt-4">
          <div className="col-12">
            <div className="card-body">
              <button
                className="btn btn-primary"
                onClick={openUploadModal}
              >
                <i className="bi bi-plus-lg"></i> Subir Documento
              </button>
            </div>
          </div>
        </div>

        <div id="documentList" className="mt-4">
          <h5 className="mb-3">Documentos Subidos</h5>
          <ul className="list-group">
            {documents.map((doc, index) => (
              <li key={index} className="list-group-item">
                <strong>{doc.title}</strong><br />
                <em>{doc.description}</em><br />
                <small>{doc.date}</small><br />
                <button
                  className="btn btn-sm btn-info me-2"
                  onClick={() => {
                    setPreviewUrl(`http://localhost:4000/uploads/${doc.filename}`);
                    setShowPreviewModal(true);
                  }}
                >
                  Vista Previa
                </button>
                <a
                  className="btn btn-sm btn-secondary"
                  href={`http://localhost:4000/uploads/${doc.filename}`} // Construir la URL manualmente
                  download={doc.title} // Nombre del archivo al descargar
                >
                  Descargar
                </a>
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* Modal de Subida */}
      {showUploadModal && (
        <div className="modal fade show d-block" tabIndex="-1" role="dialog">
          <div className="modal-dialog">
            <div className="modal-content">
              <form onSubmit={handleUpload}>
                <div className="modal-header">
                  <h5 className="modal-title">Subir Documento</h5>
                  <button
                    type="button"
                    className="btn-close"
                    onClick={() => setShowUploadModal(false)}
                  ></button>
                </div>
                <div className="modal-body">
                  <div className="mb-3">
                    <label className="form-label">Título</label>
                    <input
                      type="text"
                      className="form-control"
                      value={title}
                      onChange={(e) => setTitle(e.target.value)}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Descripción</label>
                    <textarea
                      className="form-control"
                      rows="3"
                      value={description}
                      onChange={(e) => setDescription(e.target.value)}
                      required
                    ></textarea>
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Archivo</label>
                    <input
                      type="file"
                      className="form-control"
                      accept=".pdf, .docx"
                      onChange={handleFileChange} // Llamar a la función
                      required
                    />
                  </div>
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowUploadModal(false)}
                  >
                    Cerrar
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Guardar
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Modal de Vista Previa */}
      {showPreviewModal && (
        <div className="modal fade show d-block" tabIndex="-1" role="dialog">
          <div className="modal-dialog modal-xl modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Vista Previa del Documento</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowPreviewModal(false)}
                ></button>
              </div>
              <div className="modal-body" style={{ height: '80vh' }}>
                {previewUrl.endsWith('.pdf') ? (
                  <iframe
                    src={previewUrl}
                    title="Vista Previa"
                    frameBorder="0"
                    style={{ width: '100%', height: '100%' }}
                  ></iframe>
                ) : (
                  <p>El archivo no se puede previsualizar. Por favor descárguelo.</p>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
