import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Document, Page } from 'react-pdf';
import './App.css';

function App() {
  const [documents, setDocuments] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [file, setFile] = useState(null);
  const [previewDoc, setPreviewDoc] = useState(null);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/documents');
      setDocuments(res.data);
    } catch (err) {
      console.error('Error al obtener documentos:', err);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('file', file);

    try {
      await axios.post('http://localhost:4000/api/documents', formData);
      setTitle('');
      setDescription('');
      setFile(null);
      fetchDocuments();
    } catch (err) {
      console.error('Error al subir documento:', err);
    }
  };

  const handlePreview = (filePath) => {
    setPreviewDoc(filePath);  // Actualiza el estado para mostrar el documento en la vista previa
  };

  return (
    <div className="container">
      <h1>Gestión Documentaria</h1>

      <form onSubmit={handleUpload}>
        <input
          type="text"
          placeholder="Título"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Descripción"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <input
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
          required
        />
        <button type="submit">Subir Documento</button>
      </form>

      <h2>Documentos Subidos</h2>
      <ul>
        {documents.map((doc) => (
          <li className="document-item" key={doc._id}>
            <strong>{doc.title}</strong><br />
            <em>{doc.description}</em><br />
            <button onClick={() => handlePreview(doc.filePath)}>Vista Previa</button> {' '}
            <a
              href={`http://localhost:4000/${doc.filePath}`}
              download
            >
              Descargar
            </a>
          </li>
        ))}
      </ul>

      {previewDoc && (
        <div className="preview-container">
          <h3>Vista Previa</h3>
          <Document
            file={`http://localhost:4000/${previewDoc}`}
            onLoadError={console.error}
          >
            <Page pageNumber={1} />
          </Document>
        </div>
      )}
    </div>
  );
}

export default App;
