import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';

function App() {
  const [documents, setDocuments] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [file, setFile] = useState(null);
  const [date, setDate] = useState('');
  const [previewUrl, setPreviewUrl] = useState('');

  const uploadModalRef = useRef(null);
  const previewModalRef = useRef(null);
  const previewFrameRef = useRef(null);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/documents');
      setDocuments(res.data);
    } catch (err) {
      console.error('Error al obtener documentos:', err);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!title || !description || !file || !date) {
      alert('Por favor complete todos los campos.');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('file', file);
    formData.append('date', date);

    try {
      await axios.post('http://localhost:4000/api/documents', formData);
      setTitle('');
      setDescription('');
      setFile(null);
      setDate('');
      fetchDocuments();
      const modal = window.bootstrap.Modal.getInstance(uploadModalRef.current);
      modal.hide();
    } catch (err) {
      console.error('Error al subir documento:', err);
    }
  };

  const openPreview = (url) => {
    setPreviewUrl(url);
    const modal = new window.bootstrap.Modal(previewModalRef.current);
    modal.show();
  };

  return (
    <div className="app-content">
      <div className="container-fluid">
        <div className="row mt-4">
          <div className="col-12">
            <div className="card-body">
              <button
                className="btn btn-primary"
                data-bs-toggle="modal"
                data-bs-target="#uploadDocumentModal"
              >
                <i className="bi bi-plus-lg"></i> Subir Documento
              </button>
            </div>
          </div>
        </div>

        <div id="documentList" className="mt-4">
          <h5 className="mb-3">Documentos Subidos</h5>
          <div className="row">
            {documents.map((doc) => (
              <div key={doc._id} className="col-md-6 col-lg-4 mb-3">
                <div className="card shadow-sm border-0">
                  <div className="card-body d-flex justify-content-between align-items-start">
                    <div>
                      <h5 className="card-title mb-1">
                        <i className="bi bi-file-earmark-text me-2 text-primary"></i>
                        {doc.title}
                      </h5>
                      <h6 className="card-subtitle mb-2 text-muted">
                        <i className="bi bi-calendar-event me-1"></i>
                        {new Date(doc.date).toLocaleDateString()}
                      </h6>
                      <p className="card-text mt-2">{doc.description}</p>
                    </div>
                    <div className="btn-group">
                      <button
                        className="btn btn-outline-secondary btn-sm me-2"
                        onClick={() => openPreview(`http://localhost:4000/${doc.filePath}`)}
                      >
                        <i className="bi bi-eye"></i>
                      </button>
                      <a
                        href={`http://localhost:4000/${doc.filePath}`}
                        download={doc.title}
                        className="btn btn-outline-success btn-sm"
                      >
                        <i className="bi bi-download"></i>
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Modal de Subida */}
      <div
        className="modal fade"
        id="uploadDocumentModal"
        tabIndex="-1"
        ref={uploadModalRef}
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <form onSubmit={handleUpload}>
              <div className="modal-header">
                <h5 className="modal-title">Subir Documento</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
                <div className="mb-3">
                  <label className="form-label">Título</label>
                  <input
                    type="text"
                    className="form-control"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Descripción</label>
                  <textarea
                    className="form-control"
                    rows="3"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    required
                  ></textarea>
                </div>
                <div className="mb-3">
                  <label className="form-label">Archivo</label>
                  <input
                    type="file"
                    className="form-control"
                    accept=".pdf, .docx"
                    onChange={(e) => setFile(e.target.files[0])}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Fecha de Publicación</label>
                  <input
                    type="date"
                    className="form-control"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                  />
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">
                  Cerrar
                </button>
                <button type="submit" className="btn btn-primary">
                  Guardar
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>

      {/* Modal de Vista Previa */}
      <div
        className="modal fade"
        id="previewModal"
        tabIndex="-1"
        ref={previewModalRef}
        aria-hidden="true"
      >
        <div className="modal-dialog modal-xl modal-dialog-centered">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Vista Previa del Documento</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Cerrar" />
            </div>
            <div className="modal-body" style={{ height: '80vh' }}>
              <iframe
                ref={previewFrameRef}
                src={previewUrl}
                title="Vista Previa"
                frameBorder="0"
                style={{ width: '100%', height: '100%' }}
              ></iframe>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
