import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';

function App() {
  const [documents, setDocuments] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [file, setFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [showPreviewModal, setShowPreviewModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const documentsPerPage = 5;

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/documents');
      setDocuments(res.data);
    } catch (err) {
      console.error('Error al obtener documentos:', err);
    }
  };

  const indexOfLastDocument = currentPage * documentsPerPage;
  const indexOfFirstDocument = indexOfLastDocument - documentsPerPage;
  const currentDocuments = documents.slice(indexOfFirstDocument, indexOfLastDocument);

  const totalPages = Math.ceil(documents.length / documentsPerPage);

  const nextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="app-content">
      <div className="container-fluid">
        <div className="row mt-4">
          <div className="col-12">
            <div className="card-body">
              <button
                className="btn btn-primary"
                onClick={() => setShowUploadModal(true)}
              >
                <i className="bi bi-plus-lg"></i> Subir Documento
              </button>
            </div>
          </div>
        </div>

        <div id="documentList" className="mt-4">
          <h5 className="mb-3">Documentos Subidos</h5>
          <ul className="list-group">
            {currentDocuments.map((doc, index) => (
              <li key={index} className="list-group-item">
                <strong>{doc.title}</strong><br />
                <em>{doc.description}</em><br />
                <small>{doc.date}</small><br />
                <button
                  className="btn btn-sm btn-info me-2"
                  onClick={() => {
                    setPreviewUrl(`http://localhost:4000/uploads/${doc.filename}`);
                    setShowPreviewModal(true);
                  }}
                >
                  Vista Previa
                </button>
                <a
                  className="btn btn-sm btn-secondary"
                  href={`http://localhost:4000/uploads/${doc.filename}`}
                  download={doc.title}
                >
                  Descargar
                </a>
              </li>
            ))}
          </ul>
          <div className="pagination mt-3">
            <button
              className="btn btn-primary me-2"
              onClick={prevPage}
              disabled={currentPage === 1}
            >
              Anterior
            </button>
            <span>PÃ¡gina {currentPage} de {totalPages}</span>
            <button
              className="btn btn-primary ms-2"
              onClick={nextPage}
              disabled={currentPage === totalPages}
            >
              Siguiente
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
