import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';

function App() {
  const [documents, setDocuments] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [file, setFile] = useState(null);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/documents');
      setDocuments(res.data);
    } catch (err) {
      console.error('Error al obtener documentos:', err);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('file', file);

    try {
      await axios.post('http://localhost:4000/api/documents', formData);
      setTitle('');
      setDescription('');
      setFile(null);
      fetchDocuments();
    } catch (err) {
      console.error('Error al subir documento:', err);
    }
  };

  return ( 
    <main className="app-main">
      <div className="app-content-header">
        <div className="container-fluid">
          <div className="row">
            <div className="col-sm-6"><h3 className="mb-0">Gestión Documentaria</h3></div>
            <div className="col-sm-6">
              <ol className="breadcrumb float-sm-end">
                <li className="breadcrumb-item"><a href="#">Home</a></li>
                <li className="breadcrumb-item active">Gestión Documentaria</li>
              </ol>
            </div>
          </div>
        </div>
      </div>

      <div className="app-content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-12">
              <div className="card p-4 mb-4">
                <form onSubmit={handleUpload}>
                  <div className="mb-3">
                    <label className="form-label">Título</label>
                    <input
                      type="text"
                      className="form-control"
                      value={title}
                      onChange={(e) => setTitle(e.target.value)}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Descripción</label>
                    <textarea
                      className="form-control"
                      value={description}
                      onChange={(e) => setDescription(e.target.value)}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Archivo</label>
                    <input
                      type="file"
                      className="form-control"
                      onChange={(e) => setFile(e.target.files[0])}
                      required
                    />
                  </div>
                  <button type="submit" className="btn btn-primary">
                    <i className="bi bi-upload me-2"></i>Subir Documento
                  </button>
                </form>
              </div>
            </div>
          </div>

          <div id="documentList" className="mt-4">
            <h5 className="mb-3">Documentos Subidos</h5>
            {documents.map((doc, index) => (
              <div className="card mb-3 shadow-sm border-0" key={index}>
                <div className="card-body d-flex justify-content-between align-items-start">
                  <div>
                    <h5 className="card-title mb-1">
                      <i className="bi bi-file-earmark-text me-2 text-primary"></i>{doc.title}
                    </h5>
                    <p className="card-text mt-2">{doc.description}</p>
                  </div>
                  <div className="btn-group">
                    <button className="btn btn-outline-secondary btn-sm me-2" onClick={() => openPreview(doc.fileURL)}>
                      <i className="bi bi-eye"></i>
                    </button>
                    <a href={doc.fileURL} download={doc.title} className="btn btn-outline-success btn-sm">
                      <i className="bi bi-download"></i>
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Modal de Vista Previa */}
      <div className="modal fade" id="previewModal" tabIndex="-1" aria-labelledby="previewModalLabel" aria-hidden="true">
        <div className="modal-dialog modal-xl modal-dialog-centered">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="previewModalLabel">Vista Previa del Documento</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Cerrar"></button>
            </div>
            <div className="modal-body" style={{ height: '80vh' }}>
              <iframe src={previewUrl} frameBorder="0" style={{ width: '100%', height: '100%' }} title="Vista Previa" />
            </div>
          </div>
        </div>
      </div>
    </main>
  );
}

export default App;
