import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';

function App() {
  const [documents, setDocuments] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [file, setFile] = useState(null);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/documents');
      setDocuments(res.data);
    } catch (err) {
      console.error('Error al obtener documentos:', err);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('file', file);

    try {
      await axios.post('http://localhost:4000/api/documents', formData);
      setTitle('');
      setDescription('');
      setFile(null);
      fetchDocuments();
    } catch (err) {
      console.error('Error al subir documento:', err);
    }
  };

  return (
    <div className="container">
      <h1>Gestión Documentaria</h1>

      <form onSubmit={handleUpload}>
        <input
          type="text"
          placeholder="Título"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Descripción"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <input
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
          required
        />
        <button type="submit">Subir Documento</button>
      </form>

      <h2>Documentos Subidos</h2>
      <ul>
        {documents.map((doc) => (
          <li className="document-item" key={doc._id}>
            <strong>{doc.title}</strong><br />
            <em>{doc.description}</em><br />
            <a
              href={`http://localhost:4000/${doc.filePath}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              Vista Previa
            </a>{' '}
            |{' '}
            <a
              href={`http://localhost:4000/${doc.filePath}`}
              download
            >
              Descargar
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
